<?php
 namespace Plugin\AmazonPayments\Entity; class AmazonOrder extends \Eccube\Entity\AbstractEntity { private $id; private $Order; private $reference_code; private $billable_amount; private $AmazonStatus; private $AmazonTradings; public function setId($id) { $this->id = $id; return $this; } public function getId() { return $this->id; } public function setOrder(\Eccube\Entity\Order $order) { $this->Order = $order; return $this; } public function getOrder() { return $this->Order; } public function setReferenceCode($referenceCode) { $this->reference_code = $referenceCode; return $this; } public function getReferenceCode() { return $this->reference_code; } public function setBillableAmount($billableAmount) { $this->billable_amount = $billableAmount; return $this; } public function getBillableAmount() { return $this->billable_amount; } public function setAmazonStatus(\Plugin\AmazonPayments\Entity\Master\AmazonStatus $AmazonStatus) { $this->AmazonStatus = $AmazonStatus; return $this; } public function getAmazonStatus() { return $this->AmazonStatus; } public function addAmazonTrading(\Plugin\AmazonPayments\Entity\AmazonTrading $AmazonTrading) { $this->AmazonTradings[] = $AmazonTrading; return $this; } public function getAmazonTradings() { return $this->AmazonTradings; } } 